<!DOCTYPE html>
<html class="no-js">
<head>
  <meta charset="utf-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <title>Preview</title>
  <link rel="stylesheet" href="about:blank">
  <style>
    html {
      background: white url(data:image/gif;base64,) no-repeat center 20%;
    }

    #wrapper {
      opacity: 0;
      background: white;
    }
  </style>
</head>
<body>
<div id="wrapper">


  <script src="http://tao.lan/tao/views/js/lib/jquery-1.8.0.min.js"></script>
  <script>
    $(window).on('load resize', function () {
      var docHeight = $(document).outerHeight(),
        parentContainer = $('.desktop-preview-iframe-container', parent.document);

      // firefox fires this twice, one time with a height of 0
      if (docHeight > 0) {
        // this affects the desktop preview only
        parentContainer.height(docHeight);

        // this affects the mobile preview only
        $('#wrapper').css({ opacity: 0 }).animate({ opacity: 1 }, 1000);
      }

    });
  </script>

</body>
</html>
